import React, { useState } from 'react';
import { Button } from 'primereact/button';

export default function DroneSetup() {
    const [droneConnected, setDroneConnected] = useState(false);
    const [batteryStatus, setBatteryStatus] = useState(null);

    const handleConnect = async () => {
        try {
            // Call the /connect API
            const response = await fetch('/connect', { method: 'POST' });
            if (response.ok) {
                setDroneConnected(true);
                alert('Drone connected successfully');
            } else {
                alert('Failed to connect to drone');
            }
        } catch (error) {
            console.error('Error connecting to drone:', error);
            alert('An error occurred while connecting to the drone');
        }
    };

    const fetchDroneStatus = async () => {
        try {
            // Call the /get-drone-status API
            const response = await fetch('/get-drone-status');
            if (response.ok) {
                const data = await response.json();
                setBatteryStatus(data.batteryStatus || 'Unknown');
            } else {
                alert('Failed to fetch drone status');
            }
        } catch (error) {
            console.error('Error fetching drone status:', error);
            alert('An error occurred while fetching the drone status');
        }
    };

    return (
        <div className="text-white">
            <div className="w-full grid justify-end pr-4">
                <span>Connect</span>
                <Button
                    className="mt-2 bg-gray-500 drop-shadow-lg text-yellow-400 hover:bg-gray-600"
                    icon={`${droneConnected ? 'pi pi-lock' : 'pi pi-lock-open'}`}
                    rounded
                    outlined
                    onClick={handleConnect}
                    severity="success"
                    size={25}
                />
            </div>

            <div className="mt-4">
                <h3>Drone and Camera Setup</h3>
                <ol className="list-decimal ml-6">
                    <li>Button to connect to the drone (above).</li>
                    <li>
                        After the drone is connected, go to the Home Tab and call the <code>/get-drone-status</code> API to get the battery status:
                        <Button
                            className="ml-2 bg-blue-500 text-white hover:bg-blue-600"
                            label="Get Drone Status"
                            onClick={fetchDroneStatus}
                        />
                    </li>
                    <li>
                        Display the connection status of the camera and drone below:
                        <div className="mt-2">
                            <p>
                                <strong>Drone Connection Status:</strong>{' '}
                                {droneConnected ? 'Connected' : 'Disconnected'}
                            </p>
                            <p>
                                <strong>Battery Status:</strong> {batteryStatus || 'N/A'}
                            </p>
                        </div>
                    </li>
                </ol>
            </div>
        </div>
    );
}
