import React, { useState } from 'react';

export default function Ardupilot() {
  const configs = ['Flight Mode', 'Standard Params', 'Advanced Params', 'Full Parameter List', 'Help'];
  const [selectedConfig, setSelectedConfig] = useState('Flight Mode'); // Single selection

  return (
    <div className="h-auto bg-banner-color">
      <div className="bg-banner-color text-white w-1/3 border flex flex-col">
        {
          configs.map((x) => (
            <span
              key={x} // Add a key to prevent React warnings
              onClick={() => setSelectedConfig(x)} // Set the selected configuration
              className={`p-2 cursor-pointer hover:font-bold ${selectedConfig === x ? 'font-bold' : ''}`} // Highlight selected
            >
              {x}
            </span>
          ))
        }
      </div>
    </div>
  );
}
