import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup, Circle } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import drone from '../images/drone.png';

export default function Map() {
    const [location, setLocation] = useState(null);
    const [userLocation, setUserLocation] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        // Check if geolocation is available in the browser
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    const { latitude, longitude } = position.coords;
                    setLocation({ latitude, longitude });
                    setUserLocation([latitude, longitude]);  // Set the user location
                },
                (error) => {
                    setError(error.message);
                }
            );
        } else {
            setError('Geolocation is not supported by this browser.');
        }
    }, []);

    const markerIcon = new L.Icon({
        iconUrl: drone,  // Use the imported image as the icon URL
        iconSize: [32, 32],  // Customize the size of the icon
        iconAnchor: [16, 32],  // Anchor point of the icon
        popupAnchor: [0, -32],  // Popup anchor point
    });

    return (
        <div>
            {
                userLocation ?
                    <MapContainer
                        style={{ height: "500px", width: "100%" }}
                        center={userLocation}
                        zoom={13}
                        scrollWheelZoom={false}
                    >
                        <TileLayer
                            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                        />
                        <Marker position={userLocation} icon={markerIcon}>
                            <Popup>
                                Your Current location
                            </Popup>
                        </Marker>

                        {/* Correctly adding the Circle component */}
                        <Circle center={userLocation} radius={1609.34} color="blue" fillColor="blue" fillOpacity={0.2} />
                    </MapContainer>
                    :
                    <p>Loading user location...</p>
            }
        </div>
    )
}
