import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup, Circle } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import drone from '../images/drone.png';

export default function Map() {
    const [location, setLocation] = useState(null);
    const [userLocation, setUserLocation] = useState(null);
    const [error, setError] = useState(null);
    const [markers, setMarkers] = useState([]); // State to store multiple markers

    useEffect(() => {
        // Check if geolocation is available in the browser
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    const { latitude, longitude } = position.coords;
                    setLocation({ latitude, longitude });
                    setUserLocation([latitude, longitude]);  // Set the user location
                },
                (error) => {
                    setError(error.message);
                }
            );
        } else {
            setError('Geolocation is not supported by this browser.');
        }
    }, []);

    // Handle map click event to add markers
    const handleMapClick = (event) => {
        const { lat, lng } = event.latlng;
        console.log('Map clicked at:', lat, lng); // Debug log to check if the event is being fired
        setMarkers((prevMarkers) => [...prevMarkers, { lat, lng }]); // Add new marker to state
    };

    const markerIcon = new L.Icon({
        iconUrl: drone,  // Use the imported image as the icon URL
        iconSize: [32, 32],  // Customize the size of the icon
        iconAnchor: [16, 32],  // Anchor point of the icon
        popupAnchor: [0, -32],  // Popup anchor point
    });

    return (
        <div>
            {
                userLocation ?
                    <MapContainer
                        style={{ height: "500px", width: "100%" }}
                        center={userLocation}
                        zoom={13}
                        scrollWheelZoom={false}
                        onClick={handleMapClick} // Add onClick handler for adding markers
                    >
                        <TileLayer
                            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                        />

                        {/* Render a marker for the user's current location */}
                        <Marker position={userLocation} icon={markerIcon}>
                            <Popup>
                                Your Current location
                            </Popup>
                        </Marker>

                        {/* Render markers stored in the state */}
                        {markers.map((marker, index) => (
                            <Marker key={index} position={[marker.lat, marker.lng]} icon={markerIcon}>
                                <Popup>
                                    A new marker at {marker.lat}, {marker.lng}
                                </Popup>
                            </Marker>
                        ))}

                        {/* Optionally add a circle */}
                        <Circle center={userLocation} radius={1609.34} color="blue" fillColor="blue" fillOpacity={0.2} />
                    </MapContainer>
                    :
                    <p>Loading user location...</p>
            }
        </div>
    );
}



