import React from 'react'
import { MapContainer, TileLayer, useMap, Popup } from 'react-leaflet'
import { Marker, Circle } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import { useState, useEffect } from 'react';

function setupMarkerIcon() {
    delete L.Icon.Default.prototype._getIconUrl;
    L.Icon.Default.prototype._getIconUrl = function (name) {
        return L.Icon.Default.imagePath + '/' + name + '.png';
    };
}


const icon = new L.Icon({
    iconUrl: 'path/to/your/icon.png', // Use your custom icon or the default icon
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
    shadowSize: [41, 41]
});


export default function Map() {
    setupMarkerIcon();
    const center = [33.6411, -117.8425]; // UCI Coordinates
    const position = [51.505, -0.09]
    const [location, setLocation] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        // Check if geolocation is available in the browser
        if (navigator.geolocation) {
            // Get the current position of the user
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    // Save the position to the state
                    const { latitude, longitude } = position.coords;
                    setLocation({ latitude, longitude });
                },
                (error) => {
                    // Handle error if geolocation fails
                    setError(error.message);
                }
            );
        } else {
            setError('Geolocation is not supported by this browser.');
        }
    }, []);


    return (
        <div>
            <MapContainer style={{ height: "500px", width: "100%" }} center={center} zoom={13} scrollWheelZoom={false}>
                <TileLayer
                    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                />
                <Marker position={center}>
                    <Popup>
                        Your Current location
                    </Popup>
                </Marker>

                {/* Correctly adding the Circle component */}
                <Circle center={center} radius={1609.34} color="blue" fillColor="blue" fillOpacity={0.2} />
            </MapContainer>
        </div>
    )
}
