import React, { useEffect } from 'react';

export default function ModelOptions({ options, selectedModels, setSelectedModels }) {
    useEffect(() => {
        // Ensure selectedModels is initialized to match the length of options
        if (selectedModels.length !== options.length) {
            setSelectedModels(Array(options.length).fill(false));
        }
    }, [options, selectedModels, setSelectedModels]);

    const onCheckboxChange = (e, index) => {
        const newCheckedStates = [...selectedModels];
        newCheckedStates[index] = e.target.checked;
        setSelectedModels(newCheckedStates);
    };

    return (
        <div>
            <div>
                {
                    options.map((x, index) => (
                        <div className='text-white' key={index}>
                            <input
                                type="checkbox"
                                checked={selectedModels[index] || false} // Prevent undefined error
                                onChange={(e) => onCheckboxChange(e, index)}
                                className="transform scale-150"
                            />
                            <label>{x}</label>
                        </div>
                    ))
                }
            </div>
        </div>
    );
}
